    address owner;
}

function getSimpleProxyStorage() pure returns (SimpleProxyStorage storage ds) {
    //keccak256("mithraeum.simpleproxy") is 89ebf6b655b5bf3480fb4246b9e8786d810726f923695e3ff9668f941b26cb1a
