// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./IRegistry.sol";
import "./tokens/resources/IResource.sol";
import "./assets/epoch/IEpoch.sol";
import "./tokens/prosperity/IProsperity.sol";
import "./tokens/units/IUnits.sol";
import "./tokens/workers/IWorkers.sol";
import "./geography/IGeography.sol";
import "./crossEpochsMemory/ICrossEpochsMemory.sol";
import "./rewardPool/IRewardPool.sol";
import "./distributions/IDistributions.sol";

/// @title World interface
/// @notice Functions to read state/modify state of game world
interface IWorld {
    /// @notice Emitted when world initialized
    /// @param registryContractAddress Registry contract address
    /// @param geographyAddress Geography contract address
    /// @param bannersAddress Mithraeum banners contract address
    /// @param blessTokenAddress Bless token address
    /// @param distributionsAddress Distributions token address
    /// @param rewardPoolAddress Reward pool address
    event WorldInitialized(
        address registryContractAddress,
        address geographyAddress,
        address bannersAddress,
        address blessTokenAddress,
        address distributionsAddress,
        address rewardPoolAddress
    );

    /// @notice Emitted when #setGameStartTime is called
    /// @param timestamp New game start time
    event GameStartTimeUpdated(uint timestamp);

    /// @notice Emitted when #setGameFinishTime is called
    /// @param timestamp New game finish time
    event GameFinishTimeUpdated(uint timestamp);

    /// @notice Emitted when world initialized or #destroyCurrentEpoch is called
    /// @param epochAddress New epoch address
    /// @param epochNumber New epoch number
    event NewWorldEpoch(address epochAddress, uint256 epochNumber);

    /// @notice Emitted after new epoch initialization
    /// @param epochNumber New epoch number
    event CurrentEpochNumberUpdated(uint256 epochNumber);

    // State variables

    /// @notice Registry
    /// @dev Immutable, initialized on creation
    function registry() external view returns (IRegistry);

    /// @notice Banners token
    /// @dev Immutable, initialized on creation
    function bannerContract() external view returns (IERC721);

    /// @notice Bless token
    /// @dev Immutable, initialized on creation
    function blessToken() external view returns (IERC20);

    /// @notice Distributions token
    /// @dev Immutable, initialized on creation
    function distributions() external view returns (IDistributions);

    /// @notice Cross epochs memory
    /// @dev Immutable, initialized on creation
    function crossEpochsMemory() external view returns (ICrossEpochsMemory);

    /// @notice Reward pool
    /// @dev Immutable, initialized on creation
    function rewardPool() external view returns (IRewardPool);

    /// @notice Game start time
    /// @dev Updated when #setGameStartTime is called
    function gameStartTime() external view returns (uint256);

    /// @notice Game finish time
    /// @dev Updated when #setGameFinishTime is called
    function gameFinishTime() external view returns (uint256);

    /// @notice Geography
    /// @dev Immutable, initialized on creation
    function geography() external view returns (IGeography);

    /// @notice Current world epoch
    /// @dev Updated when #destroy is called
    function currentEpochNumber() external view returns (uint256);

    /// @notice World epochs
    /// @dev Updated when world initialized or #destroy is called
    function epochs(uint256 epochNumber) external view returns (IEpoch);

    /// @notice Mapping containing world asset type by provided epoch number and address
    /// @dev Updated when #addWorldAsset is called
    function worldAssets(uint256 epochNumber, address worldAsset) external view returns (bytes32);

    // Functions

    /// @notice Proxy initializer
    /// @dev Called by address which created current instance
    /// @param registryContractAddress Registry contract address
    /// @param crossEpochsMemoryAddress Cross epochs memory address
    /// @param geographyAddress Geography contract address
    /// @param bannersAddress Banners token address
    /// @param blessTokenAddress Bless token address
    /// @param distributionsAddress Distributions token address
    /// @param rewardPoolAddress Reward pool address
    function init(
        address registryContractAddress,
        address crossEpochsMemoryAddress,
        address geographyAddress,
        address bannersAddress,
        address blessTokenAddress,
        address distributionsAddress,
        address rewardPoolAddress
    ) external;

    /// @notice Adds an address as world asset
    /// @dev Even though function is opened, it can only be called by factory contract
    /// @param epochNumber Epoch number
    /// @param worldAssetAddress World asset address
    /// @param assetType Asset type
    function addWorldAsset(
        uint256 epochNumber,
        address worldAssetAddress,
        bytes32 assetType
    ) external;

    /// @notice Mints workers to provided address
    /// @dev Even though function is opened, it can only be called by mighty creator
    /// @param epochNumber Epoch number
    /// @param to Address which will receive workers
    /// @param value Amount of workers to mint
    function mintWorkers(
        uint256 epochNumber,
        address to,
        uint256 value
    ) external;

    /// @notice Mints units to provided address
    /// @dev Even though function is opened, it can only be called by mighty creator
    /// @param epochNumber Epoch number
    /// @param unitName Type of unit to mint
    /// @param to Address which will receive units
    /// @param value Amount of units to mint
    function mintUnits(
        uint256 epochNumber,
        string memory unitName,
        address to,
        uint256 value
    ) external;

    /// @notice Mints resource to provided address
    /// @dev Even though function is opened, it can only be called by mighty creator
    /// @param epochNumber Epoch number
    /// @param resourceName Resource name
    /// @param to Address which will receive resources
    /// @param value Amount of resources to mint
    function mintResources(
        uint256 epochNumber,
        string memory resourceName,
        address to,
        uint256 value
    ) external;

    /// @notice Transfers multiple resources to provided address
    /// @dev Uses msg.sender as resources sender
    /// @param epochNumber Epoch number
    /// @param to An address which will receive resources
    /// @param resourcesNames Resources names
    /// @param amounts Amount of each resources to transfer
    function batchTransferResources(
        uint256 epochNumber,
        address to,
        string[] calldata resourcesNames,
        uint256[] calldata amounts
    ) external;

//    /// @notice Transfers multiple buildings tokens to provided addresses
//    /// @dev Uses msg.sender to verify is buildings tokens address transfer allowed from specified senders (for ex. settlement owner can recall its buildings tokens in certain conditions)
//    /// @param buildingAddresses Addresses of buildings
//    /// @param senders Addresses from which buildings tokens will be transferred
//    /// @param recipients Addresses which will receive buildings tokens
//    /// @param amounts Amount of each building token to transfer
//    function batchTransferBuildingTokens(
//        address[] calldata buildingAddresses,
//        address[] calldata senders,
//        address[] calldata recipients,
//        uint256[] calldata amounts
//    ) external;

    /// @notice Sets game finish time
    /// @dev Even though function is opened, it can only be called by mighty creator or reward pool
    /// @param gameFinishTime Game finish time
    function setGameFinishTime(uint256 gameFinishTime) external;

    /// @notice Sets game start time
    /// @dev Even though function is opened, it can only be called by mighty creator
    /// @param gameStartTime Game start time
    function setGameStartTime(uint256 gameStartTime) external;

    /// @notice Destroys current epoch if conditions are met
    /// @dev Anyone can call this function
    function destroyCurrentEpoch() external;
}

contracts/core/assets/settlement/ISettle.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "../building/IBuilding.sol";
import "../army/IArmy.sol";
import "../siege/ISiege.sol";
import "../zone/IZone.sol";

/// @title Settlement interface
/// @notice Functions to read state/modify state in order to get current settlement parameters and/or interact with it
interface ISettlement {
    /// @notice Emitted when new building is placed, all building are placed on settlement creation
    /// @param contractAddress New building address
    /// @param scriptName Building name
    event NewBuilding(address contractAddress, string scriptName);

    /// @notice Emitted when settlements army is created, is it created on settlement creation
    /// @param armyAddress New army address
    /// @param position Position
    event NewArmy(address armyAddress, uint32 position);

    /// @notice Emitted when siege is created on settlement if not present. During settlements lifetime multiple sieges can be created (one after another, not multiple simultaneously)
    /// @param siegeAddress New siege address
    event SiegeCreated(address indexed siegeAddress);

    /// @notice Emitted when #addGovernor or #removeGovernor is called
    /// @param currentEpoch Current governor epoch
    /// @param governorAddress Address of the governor event is applicable
    /// @param status Is governor became active/inactive
    event GovernorChanged(uint256 indexed currentEpoch, address indexed governorAddress, bool status);

    /// @notice Emitted when #removeGovernors is called
    /// @param currentEpoch New governor epoch
    event NewSettlementEpoch(uint256 currentEpoch);

    // State variables

    /// @notice Zone to which this settlement belongs
    /// @dev Immutable, initialized on the settlement creation
    function currentZone() external view returns (IZone);

    /// @notice Banner token id to which current settlement belongs
    /// @dev Immutable, initialized on the settlement creation
    function ownerTokenId() external view returns (uint256);

    /// @notice Siege of the settlement
    /// @dev If any army is sieging settlement not address(0), otherwise address(0)
    function siege() external view returns (ISiege);

    /// @notice Mapping containing settlements buildings
    /// @dev Types of buildings supported can be queried from registry
    function buildings(string memory buildingName) external view returns (IBuilding);

    /// @notice Current governors epoch
    /// @dev Modified when #removeGovernors is called
    function currentGovernorsEpoch() external view returns (uint256);

    /// @notice Current settlements governors
    /// @dev Modified when #addGovernor or #removeGovernor is called
    function governors(uint256 epoch, address isGovernor) external view returns (bool);

    /// @notice Settlements army
    /// Immutable, initialized on the settlement creation
    function army() external view returns (IArmy);

    /// @notice Extra prosperity amount gained from demilitarization of any army on this settlement
    /// @dev Used for determination amount of real prosperity this settlement has
    function extraProsperity() external view returns (uint256);

    /// @notice Position on which settlement is created
    /// @dev Immutable, initialized on the settlement creation
    function position() external view returns (uint32);

    // Functions

    /// @notice Proxy initializer
    /// @dev Called by factory contract which creates current instance
    /// @param createdWithOwnerTokenId Banner token id to which current settlement belongs
    /// @param zoneAddress Zone address to which this settlement belongs
    /// @param settlementPosition Position on which settlement is created
    function init(
        uint256 createdWithOwnerTokenId,
        address zoneAddress,
        uint32 settlementPosition
    ) external;

    /// @notice Transfers workers from settlement to building
    /// @dev Amount of workers to transfer is in 1e18 precision, however only integer amount can be transferred
    /// @param buildingAddress Address of the building transfer workers to
    /// @param amount Amount of workers to transfer
    function transferWorkers(address buildingAddress, uint256 amount) external;

    /// @notice Transfers game resource from settlement to specified address
    /// @dev In case if someone accidentally transfers game resource to the settlement
    /// @param resourceName Game resource name
    /// @param to Address that will receive resources
    /// @param amount Amount to transfer
    function transferResources(
        string memory resourceName,
        address to,
        uint256 amount
    ) external;

    /// @notice Creates new building
    /// @dev All buildings are created on settlement creation
    /// @param buildingName Building name
    /// @return buildingAddress Address of created building
    function newBuilding(string memory buildingName) external returns (address buildingAddress);

    /// @notice Calculates current fort health and damage dealt at specified timestamp
    /// @dev Uses fort production and siege parameters to forecast health and damage will be dealt at specified time
    /// @param timestamp Time at which calculate parameters
    /// @return currentHealth Health value at specified time
    /// @return damage Amount of damage dealt from fort.production.lastUpdateState to specified timestamp
    function calculateCurrentHealthAndDamage(uint256 timestamp)
        external
        view
        returns (uint256 currentHealth, uint256 damage);

    /// @notice Updates settlement health to current block
    /// @dev Can be called by everyone
    function updateCurrentHealth() external;

    /// @notice Creates empty siege
    /// @dev Can be called by everyone
    function createSiege() external;

    /// @notice Updates fort health
    /// @dev Even though function is opened it can be called only by world or world asset
    /// @param healthDiff Health delta between current value and new value
    /// @param isProduced Banner, whether health is produced or removed
    function updateFortHealth(uint256 healthDiff, bool isProduced) external;

    /// @notice Harvests all buildings
    /// @dev Can be used by everyone
    function massUpdate() external;

    /// @notice Calculates current prosperity at specified timestamp
    /// @dev Uses buildings productions to forecast amount of prosperity will settlement will have at specified time
    /// @param timestamp Time at which calculate current prosperity
    /// @return currentProsperity Amount of prosperity at specified time
    function accumulatedCurrentProsperity(uint256 timestamp) external view returns (int256 currentProsperity);

    /// @notice Calculates total siege power presented at current time
    /// @dev Updated when army add/remove units from siege
    /// @return currentSiegePower Amount of total siege power at current time
    function getCurrentSiegePower() external view returns (uint256 currentSiegePower);

    /// @notice Calculates current settlement owner
    /// @dev Settlements owner is considered an address, which holds ownerTokenId NFT
    /// @return settlementOwner Settlement owner
    function getSettlementOwner() external view returns (address settlementOwner);

    /// @notice Calculates whether provided address is settlement ruler or not
    /// @dev Settlements ruler is an address which owns settlement or an address(es) by which settlement is/are governed
    /// @param potentialRuler Address to check
    /// @return isRuler Banner, whether specified address is ruler or not
    function isRuler(address potentialRuler) external view returns (bool isRuler);

    /// @notice Extends current settlement prosperity by specified amount
    /// @dev Even though function is opened it can be called only by world or world asset
    /// @param prosperityAmount Amount of prosperity to which extend current prosperity
    function extendProsperity(uint256 prosperityAmount) external;
}

contracts/core/assets/siege/ISiege.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "../settlement/ISettlement.sol";

/// @title Siege interface
/// @notice Functions to read state/modify state in order to get current siege parameters and/or interact with it
interface ISiege {
    struct ArmyInfo {
        uint256 rewardDebt;
        uint256 points;
    }

    /// @notice Emitted when #addUnits is called
    /// @param from Army address which adds units
    /// @param settlement Settlement address of related siege
    /// @param unitsNames Unit types which were added
    /// @param unitsCount Counts of units which were added
    event UnitsAdded(address indexed from, address indexed settlement, string[] unitsNames, uint256[] unitsCount);

    /// @notice Emitted when #withdrawUnits is called
    /// @param to Army address which receives back its units
    /// @param settlement Settlement address of related siege
    /// @param unitsNames Unit types which were withdrawn
    /// @param unitsCount Counts of units which were withdrawn
    event UnitsWithdrawn(address indexed to, address indexed settlement, string[] unitsNames, uint256[] unitsCount);

    /// @notice Emitted when #addUnits or #withdrawUnits is called in order to preserve previous amount of points were farmed by the army with previous speed
    /// @param armyAddress Army address which received siege points
    /// @param pointsReceived Amount of points received
    event PointsReceived(address indexed armyAddress, uint256 pointsReceived);

    /// @notice Emitted when #claimResources is called
    /// @param armyAddress Army address which spent siege points
    /// @param pointsSpent Amount of points spent
    event PointsSpent(address indexed armyAddress, uint256 pointsSpent);

    /// @notice Emitted when #liquidate is called, emitted for every unit type that was liquidated
    /// @param armyAddress Army address which was liquidated
    /// @param unitName Unit type
    /// @param unitsLiquidated Amount of units liquidated
    event Liquidated(address armyAddress, string unitName, uint256 unitsLiquidated);

    // State variables

    /// @notice Settlement address to which this siege belongs
    /// @dev Immutable, initialized on the siege creation
    function currentSettlement() external view returns (ISettlement);

    /// @notice Mapping containing army information related to current siege
    /// @dev Updated when #addUnits, #withdrawUnits, #claimResource, #liqudate is called
    function armyInfo(address armyAddress) external view returns (uint256 rewardDebt, uint256 points);

    /// @notice Mapping containing amount of stored units in siege for specified army
    /// @dev Updated when #addUnits, #withdrawUnits, #liqudate is called
    function storedUnits(address armyAddress, string memory unitName) external view returns (uint256);

    /// @notice Last time at which siege was updated
    /// @dev Updated when siege parameters related to pointsPerShare were changed
    function lastUpdate() external view returns (uint256);

    /// @notice Amount of point per share
    /// @dev Updated when siege parameters related to armies were changed
    function pointsPerShare() external view returns (uint256);

    // Functions

    /// @notice Proxy initializer
    /// @dev Called by factory contract which creates current instance
    /// @param settlementAddress Settlement address
    function init(address settlementAddress) external;

    /// @notice Updates current siege to the current state
    /// @dev Synchronizes health up to current state, produces points for besieging armies
    function update() external;

    /// @notice Claims resources for specified points from building related to siege
    /// @dev Even though function is opened, it can be called only by world asset
    /// @param buildingAddress Address of building to rob
    /// @param points Amount of points to spend for robbing
    function claimResources(address buildingAddress, uint256 points) external;

    /// @notice Calculates total damage for provided period of time
    /// @param period Time period to use to calculate damage
    /// @return damage Total damage for provided period of time
    function getTotalDamageByPeriod(uint256 period) external view returns (uint256 damage);

    /// @notice Calculates total damage for period from lastUpdate and block.timestamp
    /// @return damage Total damage for last period
    function getTotalDamageLastPeriod() external view returns (uint256 damage);

    /// @notice Updates siege with new amount of damage fort taken
    /// @dev Even though function is opened, it can be called only by world asset
    function systemUpdate(uint256 totalDamage) external;

    /// @notice Adds units to siege
    /// @dev Even though function is opened, it can be called only by world asset
    /// @param unitsNames Unit types which will be added to siege
    /// @param unitsCount Amounts of units will be added to siege
    function addUnits(string[] memory unitsNames, uint256[] memory unitsCount) external;

    /// @notice Withdraws units from siege
    /// @dev Even though function is opened, it can be called only by world asset
    /// @param unitsNames Unit types which will be withdrawn to siege
    /// @param unitsCount Amounts of units will be withdrawn to siege
    function withdrawUnits(string[] memory unitsNames, uint256[] memory unitsCount) external;

    /// @notice Calculates if provided army address can be liquidated from current siege
    /// @dev Does not take into an account if army's battle is finished and army isn't left the battle
    /// @param armyAddress Address of the army
    /// @return canLiquidate Can army be liquidated from current siege
    function canLiquidate(address armyAddress) external view returns (bool canLiquidate);

    /// @notice Calculates amount of points army will have at specified time
    /// @dev If timestamp=0, returns value as if timestamp=block.timestamp
    /// @param armyAddress Address of the army
    /// @param timestamp Time at which calculate points
    /// @return points Amount of points army will have at specified time
    function getUserPointsOnTime(address armyAddress, uint256 timestamp) external view returns (uint256 points);

    /// @notice Returns amount of stored units for specified army in siege
    /// @dev Function returns only amounts without types, index in returned array for each unit type is same as in 'registry.getUnits'
    /// @param armyAddress Address of the army
    /// @return units Amount of units that army has in siege
    function getStoredUnits(address armyAddress) external view returns (uint256[] memory units);

    /// @notice Calculates total siege stats
    /// @dev Values are calculated for all armies that are present in siege
    /// @return power Total power that placed into siege
    /// @return supply Total supply that siege has
    function calculateTotalSiegeStats() external view returns (uint256 power, uint256 supply);

    /// @notice Liquidates army
    /// @dev Can be called by anyone, caller will receive a reward
    /// @param armyAddress Address of army to liquidate
    function liquidate(address armyAddress) external;

    /// @notice Calculates amount of points army has
    /// @dev Uses block.timestamp at #getUserPointsOnTime
    /// @param armyAddress Address of army
    /// @return points Amount of points army has
    function getUserPoints(address armyAddress) external returns (uint256 points);

    /// @notice Calculates army siege stats
    /// @dev Values are calculated for specified army that is present in siege
    /// @param armyAddress Address of army
    /// @return power Total power that army has
    /// @return supply Total supply that army has
    function calculateArmySiegeStats(address armyAddress) external returns (uint256 power, uint256 supply);
}

contracts/core/IRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

/// @title Registry interface
/// @notice Functions related to current game configuration
interface IRegistry {
    struct GameResource {
        string tokenName;
        string tokenSymbol;
        string worldResourceName;
    }

    struct GameUnit {
        string tokenName;
        string tokenSymbol;
        string worldUnitName;
    }

    struct ExtraResource {
        string resourceName;
        uint256 value;
    }

    struct UnitStats {
        uint256 weaponPowerStage1;
        uint256 armourPowerStage1;
        uint256 weaponPowerStage2;
        uint256 armourPowerStage2;
        uint256 siegePower;
        uint256 siegeMaxSupply;
        uint256 siegeSupport;
    }

    // State variables

    /// @notice An address which can configure/reconfigure current game
    /// @dev Immutable, initialized on the registry creation
    function mightyCreator() external view returns (address);

    /// @notice Mapping containing is provided address a factory contract or not
    /// @dev Updated when #setFactoryContract is called
    function isFactoryContract(address factoryAddress) external view returns (bool);

    /// @notice Mapping containing factory contracts addresses by provided asset types
    /// @dev Updated when #setFactoryContract is called
    /// @dev During new world asset creation process registry is asked for factory contract for exact world asset type, which will contain creation method for new world asset
    function factoryContracts(bytes32 scriptId) external view returns (address);

    /// @notice Mapping containing assets implementations addresses by provided asset types
    /// @dev Updated when #setScriptContractName is called
    /// @dev Every worlds assets implementation (code, not data) will be defined by value from this mapping
    function scriptContracts(bytes32 scriptId) external view returns (address);

    /// @notice Mapping containing units stats by provided unit types
    /// @dev Updated when #setUnitStats is called
    function unitsStats(string memory unitName)
        external
        view
        returns (
            uint256 weaponPowerStage1,
            uint256 armourPowerStage1,
            uint256 weaponPowerStage2,
            uint256 armourPowerStage2,
            uint256 siegePower,
            uint256 siegeMaxSupply,
            uint256 siegeSupport
        );

    /// @notice Global multiplier
    /// @dev Immutable, initialized on the registry creation
    function globalMultiplier() external view returns (uint256);

    /// @notice Settlement starting price
    /// @dev Immutable, initialized on the registry creation
    function settlementStartingPrice() external view returns (uint256);

    // Functions

    /// @notice Proxy initializer
    /// @dev Called by address which created current instance
    /// @param globalMultiplier Global multiplier
    /// @param settlementStartingPrice Settlement starting price
    function init(
        uint256 globalMultiplier,
        uint256 settlementStartingPrice
    ) external;

    /// @notice Sets provided address as factory contract for provided asset type
    /// @dev Even though function is opened, it can be called only by mightyCreator
    /// @param assetType Type of the asset
    /// @param factoryAddress Factory address
    function setFactoryContract(bytes32 assetType, address factoryAddress) external;

    /// @notice Sets provided address as implementation for provided asset group and asset type (for ex. group - "settlement", type - "CULTISTS")
    /// @dev Even though function is opened, it can be called only by mightyCreator
    /// @param assetGroup Asset group
    /// @param assetType Type of the asset
    /// @param implementationAddress Implementation address
    function setScriptContractName(
        string memory assetGroup,
        string memory assetType,
        address implementationAddress
    ) external;

    /// @notice Sets units stats for provided unit type
    /// @dev Even though function is opened, it can be called only by mightyCreator
    /// @param unitName Unit type
    /// @param unitStats Unit stats struct
    function setUnitStats(string calldata unitName, UnitStats memory unitStats) external;

    /// @notice Calculates worker capacity coefficient for provided building type
    /// @dev Used for internal calculation of max workers for each building
    /// @param buildingName Building type
    /// @return workerCapacityCoefficient Worker capacity coefficient
    function getWorkerCapacityCoefficient(string memory buildingName) external pure returns (uint256 workerCapacityCoefficient);

    /// @notice Calculates basic production building coefficient
    /// @dev used for internal calculation of production result
    /// @param buildingName Building type
    /// @return basicProductionBuildingCoefficient Basic production building coefficient
    function getBasicProductionBuildingCoefficient(string memory buildingName) external pure returns (uint256 basicProductionBuildingCoefficient);

    /// @notice Calculates if provided building has starting treasury on creation
    /// @dev Used for determination if treasury should be filled on settlement creation
    /// @param buildingName Building type
    /// @return hasStartingTreasury If treasury should be filled
    function hasStartingTreasury(string memory buildingName) external pure returns (bool hasStartingTreasury);

    /// @notice Calculates toxicity by resource ratio
    /// @dev Used for minting/burning toxicity
    /// @param resourceName Resource name
    /// @return toxicity Amount of toxicity per 1 resource (both are in 1e18 precision)
    function getToxicityByResource(string memory resourceName) external pure returns (uint256 toxicity);

    /// @notice Calculates resource weight
    /// @dev Used for calculation how much prosperity will be produced by resource in treasury
    /// @param resourceName Resource name
    /// @return resourceWeight Resource weight (in 1e0 precision)
    function getResourceWeight(string memory resourceName) external pure returns (uint256 resourceWeight);

    /// @notice Returns siege power to siege siege points multiplier
    /// @dev Used for determination how much siege points will be given
    /// @return siegePowerToSiegePointsMultiplier Siege power to siege siege points multiplier (in 1e18 precision)
    function getSiegePowerToSiegePointsMultiplier() external pure returns (uint256 siegePowerToSiegePointsMultiplier);

    /// @notice Returns siege point multiplier by provided resource
    /// @dev Used in calculation how many resources can be exchanged for siege points
    /// @param resourceName Resource name
    /// @return siegePointsToResourceMultiplier Siege point multiplier (in 1e18 precision)
    function getSiegePointsToResourceMultiplier(string memory resourceName) external view returns (uint256 siegePointsToResourceMultiplier);

    /// @notice Returns robbery fee
    /// @dev Used in determination how much of resource will be burned during robbery
    /// @return robberyFee Robbery fee (in 1e18 precision, where 1e18 is 100%)
    function getRobberyFee() external view returns (uint256 robberyFee);

    /// @notice Returns global multiplier
    /// @dev Used everywhere, where time is involved. Essentially determines game speed
    /// @return globalMultiplier Global multiplier
    function getGlobalMultiplier() external view returns (uint256 globalMultiplier);

    /// @notice Returns production to treasury percent
    /// @dev Determines how much of buildings production will go to treasury (if not full)
    /// @param toTreasuryPercent Production to treasury percent (in 1e18 precision, where 1e18 is 100%)
    function getToTreasuryPercent() external pure returns (uint256 toTreasuryPercent);

    /// @notice Returns base battle duration
    /// @dev Used internally to determine how long battle will last
    /// @return baseBattleDuration Base battle duration
    function getBaseBattleDuration() external view returns (uint256 baseBattleDuration);

    /// @notice Returns battle lobby phase duration percent (in 1e18 precision)
    /// @dev Used internally to determine how long lobby phase will last
    /// @return battleLobbyDurationPercent Battle lobby phase duration percent
    function getBattleLobbyDurationPercent() external view returns (uint256 battleLobbyDurationPercent);

    /// @notice Returns battle duration stun multiplier
    /// @dev Used internally to determine how long stun will last after army lost battle
    /// @return battleDurationStunMultiplier Battle duration stun multiplier
    function getBattleDurationStunMultiplier() external pure returns (uint256 battleDurationStunMultiplier);

    /// @notice Returns movement duration stun multiplier
    /// @dev Used internally to determine how long stun will last after armies' movement
    /// @return movementDurationStunMultiplier Movement duration stun multiplier
    function getMovementDurationStunMultiplier() external pure returns (uint256 movementDurationStunMultiplier);

    /// @notice Returns game buildings
    /// @dev Used internally to determine which buildings will be created on placing settlement
    /// @return buildings Buildings
    function getBuildings() external view returns (string[] memory buildings);

    /// @notice Returns game resources
    /// @dev Used internally to determine upgrade costs and providing initial resources for settlement owner based on his tier
    /// @param resources Game resources
    function getResources() external view returns (string[] memory resources);

    /// @notice Returns game units
    /// @dev Used internally in many places where interaction with units is necessary
    /// @return units Game units
    function getUnits() external view returns (string[] memory units);

    /// @notice Returns game resources
    /// @dev Used internally to determine upgrade costs and providing initial resources for settlement owner based on his tier
    /// @param resources Game resources
    function getGameResources() external view returns (GameResource[] memory resources);

    /// @notice Returns game units
    /// @dev Used internally in many places where interaction with units is necessary
    /// @return units Game units
    function getGameUnits() external view returns (GameUnit[] memory units);

    /// @notice Returns unit hiring fort hp multiplier
    /// @dev Used to determine how much units in army can be presented based on its current fort hp and this parameter
    /// @return unitHiringFortHpMultiplier Unit hiring fort hp multiplier
    function getUnitHiringFortHpMultiplier() external pure returns (uint256 unitHiringFortHpMultiplier);

    /// @notice Returns how much food unit can take from treasury to increase its army movement speed
    /// @dev Used internally to calculate army's movement speed
    /// @param unitType Unit type
    /// @return unitMaxFoodToSpendOnMove Maximum amount of food to spend on move (in 1e18 precision)
    function getUnitMaxFoodToSpendOnMove(string memory unitType) external pure returns (uint256 unitMaxFoodToSpendOnMove);

    /// @notice Returns how much prosperity will be given for provided unit type
    /// @dev Used internally to calculate how much prosperity will be given
    /// @return prosperityPerUnit Prosperity amount per one unit (in 1e18 precision)
    function getProsperityForDemilitarization(string memory unitType) external pure returns (uint256 prosperityPerUnit);

    /// @notice Returns cultists summon delay
    /// @dev Used to determine is cultists can be re-summoned
    /// @return cultistsSummonDelay Cultists summon delay (in seconds)
    function getCultistsSummonDelay() external pure returns (uint256 cultistsSummonDelay);

    /// @notice Returns max settlement that can be placed in one zone
    /// @dev Cultists does not count (so +1 with cultists)
    /// @return maxSettlementPerZone Max settlement that can be placed in one zone
    function getMaxSettlementPerZone() external pure returns (uint256 maxSettlementPerZone);

    /// @notice Returns interval duration where world is not destructible after recent cultists summon
    /// @dev Used to determine if destruction is available or not
    /// @return cultistsNoDestructionDelay No destruction interval duration (in seconds)
    function getCultistsNoDestructionDelay() external pure returns (uint256 cultistsNoDestructionDelay);

    /// @notice Returns value of cultists per zone which determines cultists threshold for world destruction
    /// @dev Used to determine amount of cultists that have to be present for world destruction
    /// @return cultistsPerZoneMultiplier Value of cultists per zone
    function getCultistsPerZoneMultiplier() external pure returns (uint256 cultistsPerZoneMultiplier);

    /// @notice Returns maximum amount of cultists that can be present in zone
    /// @dev Used to determine how many cultists will be summoned
    /// @return maxCultistsPerZone Maximum amount of cultists
    function getMaxCultistsPerZone() external pure returns (uint256 maxCultistsPerZone);

    /// @notice Returns unit type of cultists army
    /// @dev Determines type of unit in cultists army
    /// @return cultistUnitType Cultists unit type
    function getCultistUnitType() external pure returns (string memory cultistUnitType);

    /// @notice Returns building token transfer threshold percent
    /// @dev Used to determine is building token transfer allowed based on treasury percent
    /// @return buildingTokenTransferThresholdPercent Building token transfer threshold percent
    function getBuildingTokenTransferThresholdPercent() external pure returns (uint256 buildingTokenTransferThresholdPercent);

    /// @notice Returns extra resources which will be minted to user when new settlement is placed
    /// @dev During settlement creation continent contract uses output from this function to determine how much extra resources to mint
    /// @return extraResources Extra resources which will be minted to user when new settlement is placed
    function getNewSettlementExtraResources() external view returns (ExtraResource[] memory extraResources);

    /// @notice Returns new settlement starting price in settlements market
    /// @dev Used to determine how much base price for first settlement will be
    /// @return newSettlementStartingPrice New settlement starting price
    function getNewSettlementStartingPrice() external view returns (uint256 newSettlementStartingPrice);

    /// @notice Returns amount of production ticks
    /// @dev Used for production calculation
    /// @return ticks Amount of production ticks
    function getProductionTicksInSecond() external view returns (uint256 ticks);

    /// @notice Returns army demilitarization cooldown in seconds
    /// @dev Used for army demilitarization restriction
    /// @return cooldown Demilitarization cooldown in seconds
    function getDemilitarizationCooldown() external pure returns (uint256 cooldown);

    /// @notice Returns unit pool price drop per second for provided unit type, provided as numerator and denominator
    /// @dev Used for determination of current unit pool price
    /// @param unitType Unit type
    /// @return numerator Numerator
    /// @return denominator Denominator
    function getUnitPriceDropByUnitType(string memory unitType) external pure returns (uint256 numerator, uint256 denominator);
}

