contracts/core/assets/building/IBuilding.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "../../IWorld.sol";
import "../settlement/ISettlement.sol";

/// @title Building interface
/// @notice Functions to read state/modify state in order to get current building parameters and/or interact with it
interface IBuilding {
    struct BasicProduction {
        uint256 level;
        uint256 coefficient;
    }

    struct AdvancedProduction {
        uint256 level;
        uint256 coefficient;
    }

    struct Production {
        uint256 lastUpdateStateTime;
        uint256 lastUpdateStateZoneTime;
        uint256 extraTicksAfterFinish;
        uint256 productionFinishZoneTime;
        uint256 readyToBeDistributed;
    }

    struct ProductionResultItem {
        string resourceName;
        uint256 balanceChanges;
        bool isProducing;
    }

    struct InitialResourceBlock {
        string resourceName;
        uint256 perTick;
        bool isProducing;
    }

    /// @notice Emitted when #startBasicUpgrade or #startAdvancedUpgrade is called
    /// @param stateLevel New building level
    event UpgradeFinish(uint256 stateLevel);

    /// @notice Emitted when #distribute is called. When resources from production are distributed to building token holders. Will be deprecated in favor of ERC20 transfer event.
    /// @param resourceName Name of resource distributed
    /// @param holder Receiver address
    /// @param amount Amount of distributed resources
    event DistributedToShareHolder(string resourceName, address holder, uint256 amount);

    /// @notice Emitted when #setDefaultDistribution is called
    /// @param distributionId Newly created distribution id
    /// @param previousReceivers Previous distribution receivers
    event NewDistribution(uint256 distributionId, address[] previousReceivers);

    // State variables

    /// @notice Settlement address to which this building belongs
    /// @dev Immutable, initialized on the building creation
    function currentSettlement() external view returns (ISettlement);

    /// @notice Basic production
    /// @dev Contains basic production upgrade data
    /// @return level Basic production level
    /// @return coefficient Basic production coefficient
    function basicProduction() external view returns (
        uint256 level,
        uint256 coefficient
    );

    /// @notice Advanced production
    /// @dev Contains advanced production upgrade data
    /// @return level Advanced production level
    /// @return coefficient Advanced production coefficient
    function advancedProduction() external view returns (
        uint256 level,
        uint256 coefficient
    );

    /// @notice Upgrade cooldown finish time
    /// @dev Updated when #startBasicUpgrade or #startAdvancedUpgrade is called
    /// @return upgradeCooldownFinishTime Upgrade cooldown finish time
    function upgradeCooldownFinishTime() external view returns (uint256 upgradeCooldownFinishTime);

    /// @notice Buildings prosperity
    /// @dev Contains last written prosperity amount in building
    /// @return prosperity Current building prosperity
    function prosperity() external view returns (uint256 prosperity);

    /// @notice Contains current production state of the building
    /// @dev Contains information related to how production is calculated
    /// @return lastUpdateStateTime Time at which last #updateState is called
    /// @return lastUpdateStateZoneTime Zone time at which last #updateState is called
    /// @return extraTicksAfterFinish Amount of extra resource ticks will be performed after 'productionFinishZoneTime'. Implication of this parameter is special case when there are resources for production however they are produced in less than a second.
    /// @return productionFinishZoneTime Zone time at which production will stop
    /// @return readyToBeDistributed Amount of produced resource ready to be distributed
    function production()
        external
        view
        returns (
            uint256 lastUpdateStateTime,
            uint256 lastUpdateStateZoneTime,
            uint256 extraTicksAfterFinish,
            uint256 productionFinishZoneTime,
            uint256 readyToBeDistributed
        );

    /// @notice Distribution id
    /// @dev Initialized on creation and updated on #resetDistribution
    function distributionId() external view returns (uint256);

    /// @notice Produced resource debt
    /// @dev Updated when #distributeToSingleHolder or #distributeToAllShareholders is called
    function producedResourceDebt(address holder) external view returns (uint256);

    // Functions

    /// @notice Proxy initializer
    /// @dev Called by factory contract which creates current instance
    /// @param settlementAddress Settlement address
    function init(address settlementAddress) external;

    /// @notice Resets current building distribution
    /// @dev Creates new distribution Nft and mints it to current settlement owner
    function resetDistribution() external;

    /// @notice Callback which recalculates production. Called when workers or resources, which related to production of this building, is transferred from/to this building
    /// @dev Even though function is opened, it is auto-called by transfer method. Standalone calls provide 0 impact.
    function productionChanged() external;

    /// @notice Updates state of this building up to block.timestamp
    /// @dev Updates building production minting treasury and increasing #production.readyToBeDistributed
    function updateState() external;

    /// @notice Fixes debt from shareholder whenever its share part changes
    /// @dev Even though function is opened, it can be called only by distributions
    /// @param from From address
    /// @param to To address
    /// @param amount Amount
    function fixDebtAccordingToNewDistributionsAmounts(
        address from,
        address to,
        uint256 amount
    ) external;

    /// @notice Distributes produced resource to single shareholder
    /// @dev Useful to taking part of the resource from the building for single shareholder (to not pay gas for minting for all shareholders)
    /// @param holder Holder
    function distributeToSingleShareholder(address holder) external;

    /// @notice Distributes produces resource to all shareholders
    /// @dev Useful to get full produced resources to all shareholders
    function distributeToAllShareholders() external;

    /// @notice Calculates amount of workers currently sitting in this building
    /// @dev Same as workers.balanceOf(buildingAddress)
    /// @return workersAmount Amount of workers currently sitting in this building
    function getWorkers() external view returns (uint256 workersAmount);

    /// @notice Calculates real amount of provided resource in building related to its production at provided time
    /// @dev Useful for determination how much of production resource (either producing and spending) at the specific time
    /// @param resourceName Name of resource related to production
    /// @param timestamp Time at which calculate amount of resources in building. If timestamp=0 -> calculates as block.timestamp
    /// @return resourcesAmount Real amount of provided resource in building related to its production at provided time
    function getResourcesAmount(string memory resourceName, uint256 timestamp) external view returns (uint256 resourcesAmount);

    /// @notice Calculates production resources changes at provided time
    /// @dev Useful for determination how much of all production will be burned/produced at the specific time
    /// @param timestamp Time at which calculate amount of resources in building. If timestamp=0 -> calculates as block.timestamp
    /// @return productionResult Production resources changes at provided time
    function getProductionResult(uint256 timestamp) external view returns (ProductionResultItem[] memory productionResult);

    /// @notice Calculates upgrade price by provided level
    /// @dev Useful for determination how much upgrade will cost at any level
    /// @param level Level at which calculate price
    /// @return price Amount of resources needed for upgrade
    function getUpgradePrice(uint256 level) external view returns (uint256 price);

    /// @notice Calculates basic upgrade duration for provided level
    /// @dev If level=1 then returned value will be duration which is taken for upgrading from 1 to 2 level
    /// @param level At which level calculate upgrade duration
    /// @return upgradeCooldownDuration Upgrade cooldown duration
    function getBasicUpgradeCooldownDuration(uint256 level) external view returns (uint256 upgradeCooldownDuration);

    /// @notice Calculates advanced upgrade duration for provided level
    /// @dev If level=1 then returned value will be duration which is taken for upgrading from 1 to 2 level
    /// @param level At which level calculate upgrade duration
    /// @return upgradeCooldownDuration Upgrade cooldown duration
    function getAdvancedUpgradeCooldownDuration(uint256 level) external view returns (uint256 upgradeCooldownDuration);

    /// @notice Starts basic building upgrade
    /// @dev Resources required for upgrade will be taken from msg.sender
    function startBasicUpgrade() external;

    /// @notice Starts advanced building upgrade
    /// @dev Resources required for upgrade will be taken from msg.sender
    function startAdvancedUpgrade() external;

    /// @notice Calculates current level
    /// @dev Takes into an account if upgrades are finished or not
    /// @return level Current building level
    function getBuildingLevel() external view returns (uint256 level);

    /// @notice Returns production config for current building
    /// @dev Main config that determines which resources is produced/spend by production of this building
    /// @dev InitialResourceBlock.perTick is value how much of resource is spend/produced by 1 worker in 1 second of production
    /// @return initialResourceBlocks Production config for current building
    function getConfig() external view returns (InitialResourceBlock[] memory initialResourceBlocks);

    /// @notice Transfers workers from current building to specified address
    /// @dev Currently workers can be transferred from building only to its settlement
    /// @param to An address transfer workers to
    /// @param amount Amount of workers to transfer
    function transferWorkers(
        address to,
        uint256 amount
    ) external;

    /// @notice Transfer specified resource from current building
    /// @dev Used for withdrawing resources from production or overcapped treasury resources
    /// @param resourceName Name of resource to transfer
    /// @param to An address transfer resources to
    /// @param amount Amount of resources to transfer
    function transferResources(
        string memory resourceName,
        address to,
        uint256 amount
    ) external;

    /// @notice Batch resource transfer
    /// @dev Same as #transferResources but for many resources at once
    /// @param resourcesNames Names of resources to transfer
    /// @param to An address transfer resources to
    /// @param amounts Amounts of resources to transfer
    function batchTransferResources(
        string[] calldata resourcesNames,
        address to,
        uint256[] calldata amounts
    ) external;

    /// @notice Calculates maximum amount of treasury by provided level
    /// @dev Can be used to determine maximum amount of treasury by any level
    /// @param level Building level
    /// @param maxTreasury Maximum amount of treasury
    function getMaxTreasuryByLevel(uint256 level) external view returns (uint256 maxTreasury);

    /// @notice Steals resources from treasury
    /// @dev Called by siege or building owner, in either case part of resources will be burned according to #registry.getRobberyFee
    /// @param to An address which will get resources
    /// @param amount Amount of resources to steal, 'to' will get only part of specified 'amount', some percent of specified 'amount' will be burned
    /// @return realAmount Real amount of resources from which stealing occurred (min(amount, treasury))
    function stealTreasury(
        address to,
        uint256 amount
    ) external returns (uint256 realAmount);

    /// @notice Burns building treasury
    /// @dev Can be called by world asset or building owner
    /// @param amount Amount of resources to burn from treasury
    function burnTreasury(
        uint256 amount
    ) external;

    /// @notice Calculates maximum amount of workers
    /// @dev Useful to determinate maximum amount of workers
    /// @return workersAmount Maximum amount of workers
    function getMaxWorkers() external view returns (uint256 workersAmount);

    /// @notice Calculates producing resource name for this building
    /// @dev Return value is value from #getConfig where 'isProduced'=true
    /// @return resourceName Name of producing resource
    function getProducingResourceName() external view returns (string memory resourceName);

    /// @notice Calculates treasury amount at specified time
    /// @dev Useful for determination how much treasury will be at specific time
    /// @param timestamp Time at which calculate amount of treasury in building. If timestamp=0 -> calculates as block.timestamp
    /// @return treasury Treasury amount at specified time
    function getTreasury(uint256 timestamp) external view returns (uint256 treasury);

    /// @notice Calculates if building is capable to accept resource
    /// @dev Return value based on #getConfig
    /// @param resourceName Name of resource
    /// @return isResourceAcceptable Is building can accept resource
    function isResourceAcceptable(string memory resourceName) external view returns (bool isResourceAcceptable);

    /// @notice Returns building name
    /// @dev Same value as #assetName
    /// @return buildingName Building name
    function buildingName() external view returns (string memory buildingName);
}

contracts/core/assets/zone/IZone.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "../workersPool/IWorkersPool.sol";
import "../epoch/IEpoch.sol";
import "../unitsPool/IUnitsPool.sol";
import "../settlement/ISettlement.sol";
import "../settlementsMarket/ISettlementMarket.sol";

/// @title Zone interface
/// @notice Functions to read state/modify state in order to get current zone parameters and/or interact with it
interface IZone {
    /// @notice Emitted when zone initialized
    /// @param workersPoolAddress Workers pool address
    event WorkersPoolCreated(address workersPoolAddress);

    /// @notice Emitted when zone initialized
    /// @param settlementsMarketAddress Settlements market address
    event SettlementsMarketCreated(address settlementsMarketAddress);

    /// @notice Emitted when zone initialized
    /// @param unitsPoolAddress Units pool address
    /// @param unitType Unit type
    event UnitsPoolCreated(address unitsPoolAddress, string unitType);

    /// @notice Emitted when #increaseToxicity is called
    /// @param settlementAddress An address of settlement which triggered toxicity increase
    /// @param value Amount of added toxicity
    event ToxicityIncreased(address settlementAddress, uint256 value);

    /// @notice Emitted when #decreaseToxicity is called
    /// @param settlementAddress An address of settlement which triggered toxicity decrease
    /// @param value Amount of subtracted toxicity
    event ToxicityDecreased(address settlementAddress, uint256 value);

    /// @notice Emitted when #updateState is called
    /// @param lastUpdateStateTime Time at which zone time changed
    /// @param lastUpdateStateZoneTime Current zone time
    event ZoneTimeChanged(uint256 lastUpdateStateTime, uint256 lastUpdateStateZoneTime);

    // State variables

    /// @notice Workers pool
    /// @dev Immutable, initialized on the zone creation
    function workersPool() external view returns (IWorkersPool);

    /// @notice Mapping containing units pool for provided unit type
    /// @dev Immutable, initialized on the zone creation
    function unitsPools(string memory unitName) external view returns (IUnitsPool);

    /// @notice Mapping containing units market for provided unit type
    /// @dev Immutable, initialized on the zone creation
    function settlementsMarket() external view returns (ISettlementsMarket);

    /// @notice Cultists settlement of this zone
    /// @dev Immutable, initialized on the zone creation
    function cultistsSettlement() external view returns (ISettlement);

    /// @notice Last time cultists were summoned in this zone
    /// @dev Updated when #summonCultists is called
    function cultistsSummonTime() external view returns (uint256);

    /// @notice Amount of toxicity in this zone
    /// @dev Updated when #increaseToxicity or #decreaseToxicity is called
    function toxicity() external view returns (int256);

    /// @notice Zone index
    /// @dev Immutable, initialized on the zone creation
    function zoneIndex() external view returns (uint256);

    /// @notice Last apply state time
    /// @dev Updated when #updateState is called
    function lastUpdateStateTime() external view returns (uint256);

    /// @notice Last apply state zone time
    /// @dev Updated when #updateState is called
    function lastUpdateStateZoneTime() external view returns (uint256);

    // Functions

    /// @notice Proxy initializer
    /// @dev Called by factory contract which creates current instance
    /// @param zoneIndex Zone index
    function init(uint256 zoneIndex) external;

    /// @notice Creates cultists settlement
    /// @dev Even though function is opened, it can only be called by world asset
    /// @param cultistsPosition Cultists position
    function createCultists(uint32 cultistsPosition) external;

    /// @notice Buys specified units for specified amount of weapons in current zone
    /// @dev msg.sender will be used as weapons payer
    /// @param settlementAddress Settlement's address army of which will receive units
    /// @param unitNames Unit types
    /// @param unitsCounts Units counts
    /// @param maxWeaponsToSell Maximum amounts of weapons to sell for each unit types
    function buyUnitsBatch(
        address settlementAddress,
        string[] memory unitNames,
        uint256[] memory unitsCounts,
        uint256[] memory maxWeaponsToSell
    ) external;

    /// @notice Summons cultists if conditions are met
    /// @dev Anyone can call this function
    function summonCultists() external;

    /// @notice Increases toxicity relative to specified resources amount
    /// @dev Even though function is opened, it can only be called by world asset
    /// @param settlementAddress An address of the settlement which triggered toxicity increase
    /// @param resourceName Resource name
    /// @param value Amount of resource
    function increaseToxicity(
        address settlementAddress,
        string memory resourceName,
        uint256 value
    ) external;

    /// @notice Decreases toxicity relative to specified resources amount
    /// @dev Even though function is opened, it can only be called by world asset
    /// @param settlementAddress An address of the settlement which triggered toxicity decrease
    /// @param resourceName Resource name
    /// @param value Amount of resource
    function decreaseToxicity(
        address settlementAddress,
        string memory resourceName,
        uint256 value
    ) external;

    /// @notice Zone cultists summon handler
    /// @dev Even though function is opened, it can only be called by world asset
    /// @param cultistsArmyAddress Cultists army address
    /// @param value Amount of cultists minted
    function handleCultistsSummoned(
        address cultistsArmyAddress,
        uint256 value
    ) external;

    /// @notice Zone cultists defeat handler
    /// @dev Even though function is opened, it can only be called by world asset
    /// @param cultistsArmyAddress Cultists army address
    /// @param value Amount of cultists burned
    function handleCultistsDefeated(
        address cultistsArmyAddress,
        uint256 value
    ) external;

    /// @notice Calculates penalty according to current cultists count
    /// @dev Uses unit.balanceOf to determine penalty
    /// @return penalty Penalty from cultists
    function getPenaltyFromCultists() external view returns (uint256 penalty);

    /// @notice Updates zone state
    /// @dev This function is called every time when production should be modified
    function updateState() external;

    /// @notice Calculates zone time with provided timestamp
    /// @dev Takes into an account previous value and current cultists penalty and extrapolates to value at provided timestamp
    /// @param timestamp Timestamp
    /// @return zoneTime Extrapolated zone time
    function getZoneTime(uint256 timestamp) external view returns (uint256 zoneTime);
}

contracts/core/assets/workersPool/IWorke.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "../zone/IZone.sol";
import "../../tokens/workers/IWorkers.sol";
import "../../tokens/prosperity/IProsperity.sol";

/// @title Zone workers pool interface
/// @notice Functions to read state/modify state in order to mint workers/swap prosperity for workers
interface IWorkersPool {
    /// @notice Emitted when #swapProsperityForExactWorkers or #swapExactProsperityForWorkers is called
    /// @param buyer The address of settlement which bought workers
    /// @param workersBought Amount of workers bought
    /// @param prosperitySpent Amount of prosperity spent
    event WorkersBought(
        address buyer,
        uint256 workersBought,
        uint256 prosperitySpent
    );

    // State variables

    /// @notice Zone to which this pool belongs
    /// @dev Immutable, initialized on the zone creation
    function currentZone() external view returns (IZone);

    /// @notice Time at which last purchase is performed
    /// @dev Updated every time when #swapProsperityForExactWorkers is called
    function lastPurchaseTime() external view returns (uint256);

    /// @notice Starting unit price
    /// @dev Updated every time when #swapProsperityForExactWorkers is called
    function startingPrice() external view returns (uint256);

    // Functions

    /// @notice Proxy initializer
    /// @dev Called by factory contract which creates current instance
    /// @param zoneAddress Zone address
    function init(
        address zoneAddress
    ) external;

    /// @notice Swaps prosperity() for exact workers()
    /// @dev Even though function is opened, it can be executed only by ISettlement because only ISettlement can have prosperity
    /// @param workersToBuy Exact amount of workers
    /// @param maxProsperityToSell Maximum amount of prosperity to be taken for exact amount of workers
    /// @return workersCount Amount of workers bought by prosperity
    function swapProsperityForExactWorkers(uint256 workersToBuy, uint256 maxProsperityToSell) external returns (uint256 workersCount);

    /// @notice Calculates input of prosperity based on output whole amount of workers
    /// @dev Returns valid output only for integer workersToBuy value
    /// @param workersToBuy Amount of workers to buy
    /// @return prosperityToSell Amount of prosperity needed for workersToBuy
    /// @return newStartingPrice New starting price
    function getAmountIn(uint256 workersToBuy) external returns (uint256 prosperityToSell, uint256 newStartingPrice);
}

contracts/core/assets/unitsPool/IUnitsPo.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "../../tokens/units/IUnits.sol";
import "../../tokens/resources/IResource.sol";
import "../zone/IZone.sol";

/// @title Zone units pool interface
/// @notice Functions to read state/modify state in order to mint units/swap weapons for units
interface IUnitsPool {
    /// @notice Emitted when #swapWeaponsForExactUnits or #swapWeaponsForExactUnitsByZone is called
    /// @param buyer The address which payed weapons
    /// @param armyAddress The address of the army which received units
    /// @param unitsBought Amount of units bought
    /// @param weaponsSpent Amount of weapons spent
    event UnitsBought(
        address buyer,
        address armyAddress,
        uint256 unitsBought,
        uint256 weaponsSpent
    );

    // State variables

    /// @notice Zone to which this pool belongs
    /// @dev Immutable, initialized on the zone creation
    function currentZone() external view returns (IZone);

    /// @notice Units type
    /// @dev Immutable, initialized on the zone creation
    function unitsType() external view returns (string memory);

    /// @notice Time at which last purchase is performed
    /// @dev Updated every time when #swapWeaponsForExactUnits or #swapWeaponsForExactUnitsByZone is called
    function lastPurchaseTime() external view returns (uint256);

    /// @notice Starting unit price
    /// @dev Updated every time when #swapWeaponsForExactUnits or #swapWeaponsForExactUnitsByZone is called
    function startingPrice() external view returns (uint256);

    // Functions

    /// @notice Proxy initializer
    /// @dev Called by factory contract which creates current instance
    /// @param zoneAddress Zone address
    /// @param unitsType Units type
    function init(
        address zoneAddress,
        string memory unitsType
    ) external;

    /// @notice Swaps weapons() for exact units()
    /// @dev msg.sender will be used as weapons payer
    /// @param settlementAddress Settlement address, army of which, will receive units
    /// @param unitsToBuy Exact amount of units
    /// @param maxWeaponsToSell Maximum amount of weapons to be taken for exact amount of units
    /// @return unitsCount Amount of units bought by weapons
    function swapWeaponsForExactUnits(
        address settlementAddress,
        uint256 unitsToBuy,
        uint256 maxWeaponsToSell
    ) external returns (uint256 unitsCount);

    /// @notice Swaps weapons() for exact units()
    /// @dev Even though function is opened it can be called only by zone
    /// @param weaponsPayer Address from which weapons will be taken
    /// @param settlementAddress Settlement address, army of which, will receive units
    /// @param unitsToBuy Exact amount of units
    /// @param maxWeaponsToSell Maximum amount of weapons to be taken for exact amount of units
    /// @return unitsCount Amount of units bought by weapons
    function swapWeaponsForExactUnitsByZone(
        address weaponsPayer,
        address settlementAddress,
        uint256 unitsToBuy,
        uint256 maxWeaponsToSell
    ) external returns (uint256 unitsCount);

    /// @notice Calculates input of weapons based on output whole amount of units
    /// @dev Returns valid output only for integer unitsToBuy value (not in 1e18 precision)
    /// @param unitsToBuy Amount of workers to buy
    /// @return weaponsToSell Amount of weapons needed for unitsToBuy
    /// @return newStartingPrice New starting price
    function getAmountIn(uint256 unitsToBuy) external returns (uint256 weaponsToSell, uint256 newStartingPrice);
}

contracts/core/assets/settlementsMarket/.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "../zone/IZone.sol";

/// @title Zone settlements market interface
/// @notice Functions to read state/modify state in order to buy settlement
interface ISettlementsMarket {
    /// @notice Emitted when #buySettlement is called
    /// @param settlementAddress Settlement address
    /// @param settlementCost Settlement cost
    event SettlementBought(address settlementAddress, uint256 settlementCost);

    // State variables

    /// @notice Zone to which this market belongs
    /// @dev Immutable, initialized on the zone creation
    function currentZone() external view returns (IZone);

    /// @notice Time at which last purchase occurred
    /// @dev Updated when #buySettlement is called
    function lastPurchaseTime() external view returns (uint256);

    // Functions

    /// @notice Proxy initializer
    /// @dev Called by factory contract which creates current instance
    /// @param zoneAddress Zone address
    function init(
        address zoneAddress
    ) external;

    /// @notice Buys settlement in zone
    /// @dev Tokens will be deducted from msg.sender
    /// @param position Position
    /// @param ownerTokenId MithraeumBanners token id which will represent to which settlement will be attached to
    /// @param maxTokensToUse Maximum amount of tokens to be withdrawn for settlement
    function buySettlement(
        uint32 position,
        uint256 ownerTokenId,
        uint256 maxTokensToUse
    ) external payable;

    /// @notice Returns amount of tokens new settlement will cost
    /// @dev Calculates cost of placing new settlement in tokens
    /// @return cost Amount of tokens new settlement will cost
    function getNewSettlementCost() external view returns (uint256 cost);
}

