pragma solidity ^0.8.13;

import "../../IWorld.sol";
import "../battle/IBattle.sol";
import "../settlement/ISettlement.sol";
import "../siege/ISiege.sol";

/// @title Army interface
/// @notice Functions to read state/modify state in order to get current army parameters and/or interact with it
interface IArmy {
    struct MovementTiming {
        uint64 startTime;
        uint64 endTime;
    }

    struct StunTiming {
        uint64 startTime;
        uint64 endTime;
    }

    /// @notice Emitted when #burnUnits is called (#demilitarize or #exitBattle)
    /// @param unitName Name of the unit type
    /// @param value New amount of unit type presented in army
    event UnitsChanged(string unitName, uint256 value);

    /// @notice Emitted when #updatePosition is called (even though event can be emitted only on the next action related to the current army, de-facto army will update position based on 'movementTiming.endTime'
    /// @param settlementAddress Address of the settlement where army currently staying on
    /// @param position Position
    event UpdatedPosition(address settlementAddress, uint32 position);

    /// @notice Emitted when #newBattle is called. Army which attacks another army will emit this event.
    /// @param battleAddress Created battle address
    /// @param targetArmyAddress Address of the attacked army
    event NewBattle(address battleAddress, address targetArmyAddress);

    /// @notice Emitted when army joins battle. At the battle creation both armies (attacker and attacked) will emit this event. Attacker army will be side A and at attacked army will be sideB
    /// @param battleAddress Address of the battle army joined in
    /// @param side Side to which army joined (sideA = 1, sideB = 2)
    event JoinedBattle(address indexed battleAddress, uint256 side);

    /// @notice Emitted when #updateState is called (even though event can be emitted only on the next action related to the current army, de-facto army will exit battle when battle is finished)
    /// @param battleAddress Address of the battle army was in
    event ExitedFromBattle(address battleAddress);

    /// @notice Emitted when #move is called
    /// @param destinationSettlement Address of the settlement army is moving to
    /// @param movementStartTime Time at which movement began
    /// @param movementFinishTime Time at which movement will end
    /// @param path The path army is taken from starting position to destination position
    event MovingTo(
        address indexed destinationSettlement,
        uint256 indexed movementStartTime,
        uint256 indexed movementFinishTime,
        uint32[] path
    );

    // State variables

    /// @notice Settlement address to which this army belongs
    /// @dev Immutable, initialized on the army creation
    function currentSettlement() external view returns (ISettlement);

    /// @notice Position where army currently stands on
    /// @dev Updated when army updates position. It does not take into account if army is moving
    /// @dev To proper query current position use #getCurrentPosition
    function currentPosition() external view returns (uint32);

    /// @notice Position to which are is moving to
    /// @dev Updated when army starts moving. It does not take into account if army is finished move by time
    /// @dev To proper calculate destination position you need to check if army finished movement by comparing current time and movementTiming.endTime
    function destinationPosition() external view returns (uint32);

    /// @notice Battle in which army is on
    /// @dev If army is not in battle returns address(0). It does not take into account if battle is finished but army is not left the battle
    function battle() external view returns (IBattle);

    /// @notice Siege in which are army is on
    /// @dev If army is not in siege returns address(0)
    function siege() external view returns (ISiege);

    /// @notice Movement timings
    /// @dev Updated when army starts moving. It does not take into account if army is finished move by time
    function movementTiming() external view returns (uint64 startTime, uint64 endTime);

    /// @notice Stun timings
    /// @dev Updated when army finishes move or when army loses battle
    function stunTiming() external view returns (uint64 startTime, uint64 endTime);

    /// @notice Path army is taken during movement
    /// @dev Updated when army starts moving. It does not take into account if army is finished move by time
    /// @dev To proper query entire movementPath use #getMovementPath
    function movementPath(uint256 index) external view returns (uint32);

    /// @notice Time at which last demilitarization occured
    /// @dev Updated when #demilitarize is called
    function lastDemilitarizationTime() external view returns (uint256);

    // Functions

    /// @notice Proxy initializer
    /// @dev Called by factory contract which creates current instance
    /// @param settlementAddress Settlement address
    function init(address settlementAddress) external;

    /// @notice Path army is taken during movement
    /// @dev Useful to get entire movement path rather than querying each path item by index. It does not take into account if army is finished move by time
    /// @return path Entire path army is taken during movement
    function getMovementPath() external view returns (uint32[] memory path);

    /// @notice Updates army state to the current block
    /// @dev Called on every action which are based on army state and time
    function updateState() external;

    /// @notice Initiates army movement to the settlement
    /// @dev Even though path can be provided artificial only allowed movement to a settlement
    /// @param path Path army will take to the settlement
    /// @param foodToSpendOnFeeding Amount of food army will take from current position settlements FARM in order to decrease total time army will take to get to destination position
    function move(uint32[] memory path, uint256 foodToSpendOnFeeding) external;

    /// @notice Demilitarizes part of the army. Demilitarization provides prosperity to the settlement army is currently staying on
    /// @dev Even though demilitarization of 0 units may seem reasonable, it is disabled
    /// @param unitNames Names of the unit types for demilitarization
    /// @param unitsCount Amount of units for demilitarization for every unit type
    function demilitarize(string[] memory unitNames, uint256[] memory unitsCount) external;

    /// @notice Sets battle
    /// @dev Can only be called by world or world asset
    /// @param battleAddress Battle address
    function setBattle(address battleAddress) external;

    /// @notice Initiates battle with another army is both are not in battle
    /// @dev Creates IBattle and sets both armies in created battle
    /// @param armyAddress Address of the army this army will attack
    /// @param maxUnitTypesToAttack Max units types to attack
    /// @param maxUnitsToAttack Max units to attack
    function newBattle(
        address armyAddress,
        string[] calldata maxUnitTypesToAttack,
        uint256[] calldata maxUnitsToAttack
    ) external;

    /// @notice Joins current army in battle to the provided side
    /// @dev Moving army is able to join battle only if caller is another army (drags it into battle)
    /// @param battleAddress Battle address army will join
    /// @param side Side of the battle army will join (sideA = 1, sideB = 2)
    function joinBattle(address battleAddress, uint256 side) external;

    /// @notice Burns units from the army
    /// @dev Can only be called by world or world asset
    /// @param unitTypes Unit types for burning
    /// @param unitAmounts Amount of units for burning for every unit type
    function burnUnits(string[] memory unitTypes, uint256[] memory unitAmounts) external;

    /// @notice Calculates current position taking to the account #movementTimings
    /// @dev This method should be used to determine real army position
    /// @return position Position
    function getCurrentPosition() external view returns (uint32 position);

    /// @notice Sets and withdraw units to/from siege
    /// @dev Provides ability to atomically setup/re-setup siege
    /// @param addUnitsNames Names of the unit types to put in siege
    /// @param addUnitsCount Amount of units to put in siege for every unit type
    /// @param removeUnitsNames Names of the unit types to withdraw from siege
    /// @param removeUnitsCount Amount of units to withdraw from siege for every unit type
    function setUnitsInSiege(
        string[] memory addUnitsNames,
        uint256[] memory addUnitsCount,
        string[] memory removeUnitsNames,
        uint256[] memory removeUnitsCount
    ) external;

    /// @notice Swaps accumulated robbery tokens in siege for resource
    /// @dev Amount of points will be taken may be lesser if building does not have resources in its treasury
    /// @param buildingAddress Address of the building treasury of which will be robbed
    /// @param points Amount of points to spend for resources
    function claimResources(address buildingAddress, uint256 points) external;

    /// @notice Calculates total siege support of the army
    /// @dev For every unit type placed in siege calculates sum of all of them
    /// @return totalSiegeSupport Total siege support of the army
    function getTotalSiegeSupport() external view returns (uint256 totalSiegeSupport);

    /// @notice Sets siege
    /// @dev Can only be called by world or world asset
    /// @param siegeAddress Siege address
    function setSiege(address siegeAddress) external;

    /// @notice Return owner of the army
    /// @dev Same as owner of the settlement to which this army belongs
    /// @return ownerAddress Address of the owner of the army
    function getOwner() external view returns (address ownerAddress);

    /// @notice Calculates is army on its home position
    /// @dev Takes into account if army movement is finished
    /// @return isHomePosition Is army on home position
    function isHomePosition() external view returns (bool isHomePosition);
}
","keccak256":"0x08001bb52d1d3e51017d7223b3adf8ac80e6038b228811aac21a064c1956a141","license":"MIT"},"contracts/core/assets/battle/IBattle.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "../../IWorld.sol";

/// @title Battle interface
/// @notice Functions to read state/modify state in order to get current battle parameters and/or interact with it
interface IBattle {
    struct Timing {
        uint64 creationTime;
        uint64 lobbyDuration;
        uint64 ongoingDuration;
        uint64 finishTime;
    }

    /// @notice Emitted when army joined battle
    /// @param armyAddress Address of the joined army
    /// @param side Side to which army is joined (sideA = 1, sideB = 2)
    event ArmyJoined(address armyAddress, uint256 side);

    /// @notice Emitted when #finishBattle is called
    /// @param finishTime Time at which battle is finished
    event BattleFinished(uint256 finishTime);

    // State variables

    /// @notice Position at which battle is being held
    /// @dev Immutable, initialized on the battle creation
    function position() external view returns (uint32);

    /// @notice An array of armies joined to side A
    /// @dev Updated when army joins side A
    /// @param index Index inside the array
    /// @return armyAddress Army address at the specified index
    function sideA(uint256 index) external view returns (address armyAddress);

    /// @notice An array of armies joined to side B
    /// @dev Updated when army joins side B
    /// @param index Index inside the array
    /// @return armyAddress Army address at the specified index
    function sideB(uint256 index) external view returns (address armyAddress);

    /// @notice Mapping that contains units amount by side and unit type
    /// @dev Updated when army joins side
    /// @param side Side of which query units amount (sideA = 1, sideB = 2)
    /// @param unitName Unit type to query
    /// @return unitsCount Amount of units by specified side and unit type
    function sideUnitsCount(uint256 side, string memory unitName) external view returns (uint256 unitsCount);

    /// @notice Mapping that contains amount of units by army address and unit type
    /// @dev Updated when army joins battle
    /// @param armyAddress Address of the army
    /// @param unitName Unit type to query
    /// @return unitsCount Amount of units by army address and unit type
    function armyUnitsCount(address armyAddress, string memory unitName) external view returns (uint256 unitsCount);

    /// @notice Mapping that contains amount of casualties after battle is finished
    /// @dev Updated when #startBattle is called
    /// @param side Side of which query casualties amount (sideA = 1, sideB = 2)
    /// @param unitName Unit type to query
    /// @return casualtiesCount Amount of casualties by side and unit type
    function casualties(uint256 side, string memory unitName) external view returns (uint256 casualtiesCount);

    /// @notice Mapping that contains side at which joined army is on
    /// @dev Updated when #acceptArmyInBattle is called
    /// @param armyAddress Address of the army
    /// @return armySide Side of specified army (sideA = 1, sideB = 2)
    function armySide(address armyAddress) external view returns (uint256 armySide);

    /// @notice Battle time parameters
    /// @dev Updated when battle initialized, first armies joined and finished (#initBattle, #acceptArmyInBattle, #startBattle)
    /// @return creationTime Time when battle is created
    /// @return lobbyDuration Lobby duration, initialized when first two armies joined
    /// @return ongoingDuration Ongoing duration, initialized when first two armies joined
    /// @return finishTime Time when battle is finished
    function timing()
        external
        view
        returns (
            uint64 creationTime,
            uint64 lobbyDuration,
            uint64 ongoingDuration,
            uint64 finishTime
        );

    /// @notice Winning side
    /// @dev Updated when #finishBattle is called
    /// @return winningSide Winning side (no winner = 0, sideA = 1, sideB = 2)
    function winningSide() external view returns (uint256 winningSide);

    // Functions

    /// @notice Proxy initializer
    /// @dev Called by factory contract which creates current instance
    /// @param attackerArmyAddress Attacker army address
    /// @param attackedArmyAddress Attacked army address
    function init(address attackerArmyAddress, address attackedArmyAddress) external;

    /// @notice Calculates amount of armies joined to side A
    /// @dev Basically returns length of sideA array
    /// @return armiesCount Amount of armies joined to side A
    function getSideALength() external view returns (uint256 armiesCount);

    /// @notice Calculates amount of armies joined to side B
    /// @dev Basically returns length of sideA array
    /// @return armiesCount Amount of armies joined to side B
    function getSideBLength() external view returns (uint256 armiesCount);

    /// @notice Accepts army in battle
    /// @dev Even though function is opened, it can only be called by world asset
    /// @param armyAddress Army address
    /// @param side Side to which army will join
    function acceptArmyInBattle(address armyAddress, uint256 side) external;

    /// @notice Removes army from battle
    /// @dev Even though function is opened, it can only be called by world asset
    /// @param armyAddress Army address
    function removeArmyFromBattle(address armyAddress) external;

    /// @notice Finishes battle
    /// @dev Sets finish time
    function finishBattle() external;

    /// @notice Calculates casualties for first battle stage
    /// @dev Uses values from battles' sideUnitsCount in order to calculate casualties (can be executed while battle is still not fully formed)
    /// @return sideACasualties Side A casualties
    /// @return sideBCasualties Side B casualties
    /// @return stageParams Stage params (encoded abi.encode(sideAOffense, sideBOffense, sideADefence, sideBDefence))
    function calculateStage1Casualties()
        external
        view
        returns (
            uint256[] memory sideACasualties,
            uint256[] memory sideBCasualties,
            bytes memory stageParams
        );

    /// @notice Calculates casualties for second battle stage (based on casualties from first battle stage)
    /// @dev Uses values from battles' sideUnitsCount in order to calculate casualties (can be executed while battle is still not fully formed)
    /// @param stage1SideACasualties Stage 1 side A casualties
    /// @param stage1SideBCasualties Stage 1 side B casualties
    /// @return sideACasualties Side A casualties
    /// @return sideBCasualties Side B casualties
    /// @return stageParams Stage params (encoded abi.encode(sideAOffense, sideBOffense, sideADefence, sideBDefence))
    function calculateStage2Casualties(
        uint256[] memory stage1SideACasualties,
        uint256[] memory stage1SideBCasualties
    )
        external
        view
        returns (
            uint256[] memory sideACasualties,
            uint256[] memory sideBCasualties,
            bytes memory stageParams
        );

    /// @notice Calculates casualties for all battle stages
    /// @dev Uses values from battles' sideUnitsCount in order to calculate casualties (can be executed while battle is still not fully formed)
    /// @return sideACasualties Side A casualties
    /// @return sideBCasualties Side A casualties
    /// @return winningSide Winning side (0 - both sides lost, 1 - side A Won, 2 - side B Won
    function calculateAllCasualties()
        external
        view
        returns (
            uint256[] memory sideACasualties,
            uint256[] memory sideBCasualties,
            uint256 winningSide
        );

    /// @notice Calculates lobby and ongoing duration
    /// @dev Returns same value as #calculateTimings but without the need to provide all parameters
    /// @param isCultistsAttacked Is cultists attacked
    /// @param maxBattleDuration Max allowed battle duration
    /// @param sideAUnitsCount Side A units count
    /// @param sideBUnitsCount Side B units count
    /// @return lobbyDuration Lobby duration
    /// @return ongoingDuration Ongoing duration
    function getTimings(
        bool isCultistsAttacked,
        uint256 maxBattleDuration,
        uint256 sideAUnitsCount,
        uint256 sideBUnitsCount
    ) external view returns (uint64 lobbyDuration, uint64 ongoingDuration);

    /// @notice Calculates if battle can be finished
    /// @dev Checks if finishTime is set and current block.timestamp > creationTime + lobbyDuration + ongoingDuration
    /// @return canFinishBattle Can battle be finished
    function canFinishBattle() external view returns (bool canFinishBattle);

    /// @notice Calculates if battle is finished
    /// @dev Checks if finishTime is not zero
    /// @return isFinishedBattle Is finished battle
    function isFinishedBattle() external view returns (bool isFinishedBattle);

    /// @notice Calculates casualties for specified army
    /// @dev Provides valid results only for finished battle
    /// @param armyAddress Address of army presented in battle
    /// @return isArmyWon Is army won
    /// @return unitAmounts Amount of casualties for related unit types
    function calculateArmyCasualties(address armyAddress)
        external
        view
        returns (
            bool isArmyWon,
            uint256[] memory unitAmounts
        );

    /// @notice Calculates if lobby is opened
    /// @dev Calculates if lobby is opened
    /// @return isLobbyTime Is lobby is opened
    function isLobbyTime() external view returns (bool isLobbyTime);

    /// @notice Calculates lobby duration and ongoing duration based on specified parameters
    /// @dev globalMultiplier, baseLobbyDuration, baseOngoingDuration parameters from registry
    /// @param globalMultiplier Global multiplier (from registry)
    /// @param baseBattleDuration Base battle duration (from registry)
    /// @param battleLobbyDurationPercent Battle lobby duration percent (from registry)
    /// @param isCultistsAttacked Is cultists attacked
    /// @param units1 Amount of units from attacker army
    /// @param units2 Amount of units from attacked army
    /// @param maxBattleDuration Max allowed battle duration
    /// @return lobbyDuration Lobby duration
    /// @return ongoingDuration Ongoing duration
    function calculateTimings(
        uint256 globalMultiplier,
        uint256 baseBattleDuration,
        uint256 battleLobbyDurationPercent,
        bool isCultistsAttacked,
        uint256 units1,
        uint256 units2,
        uint256 maxBattleDuration
    ) external view returns (uint64 lobbyDuration, uint64 ongoingDuration);
}
","keccak256":"0x26528950f07de43569411bb4647abceb9130a61de22590636e0b6edd0ff295c6","license":"MIT"},"contracts/core/assets/building/IBuilding.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "../../IWorld.sol";
import "../settlement/ISettlement.sol";

/// @title Building interface
/// @notice Functions to read state/modify state in order to get current building parameters and/or interact with it
interface IBuilding {
    struct BasicProduction {
        uint256 level;
        uint256 coefficient;
    }

    struct AdvancedProduction {
        uint256 level;
        uint256 coefficient;
        uint256 additionalWorkersCapacityMultiplier;
        uint256 toBeProducedTicks;
    }

    struct Production {
        uint256 lastUpdateStateTime;
        uint256 lastUpdateStateZoneTime;
        uint256 readyToBeDistributed;
    }

    struct ProductionResultItem {
        string resourceName;
        uint256 balanceChanges;
        bool isProducing;
    }

    struct InitialResourceBlock {
        string resourceName;
        uint256 perTick;
        bool isProducing;
    }

    /// @notice Emitted when #startBasicUpgrade or #startAdvancedUpgrade is called
    /// @param stateLevel New building level
    event UpgradeFinish(uint256 stateLevel);

    /// @notice Emitted when #distribute is called. When resources from production are distributed to building token holders. Will be deprecated in favor of ERC20 transfer event.
    /// @param resourceName Name of resource distributed
    /// @param holder Receiver address
    /// @param amount Amount of distributed resources
    event DistributedToShareHolder(string resourceName, address holder, uint256 amount);

    /// @notice Emitted when #setDefaultDistribution is called
    /// @param distributionId Newly created distribution id
    /// @param previousReceivers Previous distribution receivers
    event NewDistribution(uint256 distributionId, address[] previousReceivers);

    // State variables

    /// @notice Settlement address to which this building belongs
    /// @dev Immutable, initialized on the building creation
    function currentSettlement() external view returns (ISettlement);

    /// @notice Basic production
    /// @dev Contains basic production upgrade data
    /// @return level Basic production level
    /// @return coefficient Basic production coefficient
    function basicProduction() external view returns (
        uint256 level,
        uint256 coefficient
    );

    /// @notice Advanced production
    /// @dev Contains advanced production upgrade data
    /// @return level Advanced production level
    /// @return coefficient Advanced production coefficient
    /// @return additionalWorkersCapacityMultiplier Additional workers capacity multiplier
    /// @return toBeProducedTicks To be produced ticks of producing resource
    function advancedProduction() external view returns (
        uint256 level,
        uint256 coefficient,
        uint256 additionalWorkersCapacityMultiplier,
        uint256 toBeProducedTicks
    );

    /// @notice Upgrade cooldown finish time
    /// @dev Updated when #startBasicUpgrade or #startAdvancedUpgrade is called
    /// @return upgradeCooldownFinishTime Upgrade cooldown finish time
    function upgradeCooldownFinishTime() external view returns (uint256 upgradeCooldownFinishTime);

    /// @notice Buildings prosperity
    /// @dev Contains last written prosperity amount in building
    /// @return prosperity Current building prosperity
    function prosperity() external view returns (uint256 prosperity);

    /// @notice Contains current production state of the building
    /// @dev Contains information related to how production is calculated
    /// @return lastUpdateStateTime Time at which last #updateState is called
    /// @return lastUpdateStateZoneTime Zone time at which last #updateState is called
    /// @return readyToBeDistributed Amount of produced resource ready to be distributed
    function production()
        external
        view
        returns (
            uint256 lastUpdateStateTime,
            uint256 lastUpdateStateZoneTime,
            uint256 readyToBeDistributed
        );

    /// @notice Distribution id
    /// @dev Initialized on creation and updated on #resetDistribution
    function distributionId() external view returns (uint256);

    /// @notice Produced resource debt
    /// @dev Updated when #distributeToSingleHolder or #distributeToAllShareholders is called
    function producedResourceDebt(address holder) external view returns (uint256);

    // Functions

    /// @notice Proxy initializer
    /// @dev Called by factory contract which creates current instance
    /// @param settlementAddress Settlement address
    function init(address settlementAddress) external;

    /// @notice Resets current building distribution
    /// @dev Creates new distribution Nft and mints it to current settlement owner
    function resetDistribution() external;

    /// @notice Callback which recalculates production. Called when resources related to production of this building is transferred from/to this building
    /// @dev Even though function is opened, it is auto-called by transfer method. Standalone calls provide 0 impact.
    function productionResourcesChanged() external;

    /// @notice Updates state of this building up to block.timestamp
    /// @dev Updates building production minting treasury and increasing #production.readyToBeDistributed
    function updateState() external;

    /// @notice Fixes debt from shareholder whenever its share part changes
    /// @dev Even though function is opened, it can be called only by distributions
    /// @param from From address
    /// @param to To address
    /// @param amount Amount
    function fixDebtAccordingToNewDistributionsAmounts(
        address from,
        address to,
        uint256 amount
    ) external;

    /// @notice Distributes produced resource to single shareholder
    /// @dev Useful to taking part of the resource from the building for single shareholder (to not pay gas for minting for all shareholders)
    /// @param holder Holder
    function distributeToSingleShareholder(address holder) external;

    /// @notice Distributes produces resource to all shareholders
    /// @dev Useful to get full produced resources to all shareholders
    function distributeToAllShareholders() external;

    /// @notice Calculates building coefficient by provided level
    /// @dev Used to determine max treasury amount and new production coefficients
    /// @param level Building level
    /// @return buildingCoefficient Building coefficient
    function getBuildingCoefficient(uint256 level) external pure returns (uint256 buildingCoefficient);

    /// @notice Calculates amount of workers currently sitting in this building
    /// @dev Same as workers.balanceOf(buildingAddress)
    /// @return workersAmount Amount of workers currently sitting in this building
    function getWorkers() external view returns (uint256 workersAmount);

    /// @notice Calculates real amount of provided resource in building related to its production at provided time
    /// @dev Useful for determination how much of production resource (either producing and spending) at the specific time
    /// @param resourceName Name of resource related to production
    /// @param timestamp Time at which calculate amount of resources in building. If timestamp=0 -> calculates as block.timestamp
    /// @return resourcesAmount Real amount of provided resource in building related to its production at provided time
    function getResourcesAmount(string memory resourceName, uint256 timestamp) external view returns (uint256 resourcesAmount);

    /// @notice Calculates production resources changes at provided time
    /// @dev Useful for determination how much of all production will be burned/produced at the specific time
    /// @param timestamp Time at which calculate amount of resources in building. If timestamp=0 -> calculates as block.timestamp
    /// @return productionResult Production resources changes at provided time
    function getProductionResult(uint256 timestamp) external view returns (ProductionResultItem[] memory productionResult);

    /// @notice Calculates upgrade price by provided level
    /// @dev Useful for determination how much upgrade will cost at any level
    /// @param level Level at which calculate price
    /// @return price Amount of resources needed for upgrade
    function getUpgradePrice(uint256 level) external view returns (uint256 price);

    /// @notice Calculates basic upgrade duration for provided level
    /// @dev If level=1 then returned value will be duration which is taken for upgrading from 1 to 2 level
    /// @param level At which level calculate upgrade duration
    /// @return upgradeCooldownDuration Upgrade cooldown duration
    function getBasicUpgradeCooldownDuration(uint256 level) external view returns (uint256 upgradeCooldownDuration);

    /// @notice Calculates advanced upgrade duration for provided level
    /// @dev If level=1 then returned value will be duration which is taken for upgrading from 1 to 2 level
    /// @param level At which level calculate upgrade duration
    /// @return upgradeCooldownDuration Upgrade cooldown duration
    function getAdvancedUpgradeCooldownDuration(uint256 level) external view returns (uint256 upgradeCooldownDuration);

    /// @notice Starts basic building upgrade
    /// @dev Resources required for upgrade will be taken either from msg.sender or resourcesOwner (if resource.allowance allows it)
    /// @dev If resourcesOwner == address(0) -> resources will be taken from msg.sender
    /// @dev If resourcesOwner != address(0) and resourcesOwner has given allowance to msg.sender >= upgradePrice -> resources will be taken from resourcesOwner
    /// @param resourcesOwner Resources owner
    function startBasicUpgrade(address resourcesOwner) external;

    /// @notice Starts advanced building upgrade
    /// @dev Resources required for upgrade will be taken either from msg.sender or resourcesOwner (if resource.allowance allows it)
    /// @dev If resourcesOwner == address(0) -> resources will be taken from msg.sender
    /// @dev If resourcesOwner != address(0) and resourcesOwner has given allowance to msg.sender >= upgradePrice -> resources will be taken from resourcesOwner
    /// @param resourcesOwner Resources owner
    function startAdvancedUpgrade(address resourcesOwner) external;

    /// @notice Calculates current level
    /// @dev Takes into an account if upgrades are finished or not
    /// @return level Current building level
    function getBuildingLevel() external view returns (uint256 level);

    /// @notice Returns production config for current building
    /// @dev Main config that determines which resources is produced/spend by production of this building
    /// @dev InitialResourceBlock.perTick is value how much of resource is spend/produced by 1 worker in 1 second of production
    /// @return initialResourceBlocks Production config for current building
    function getConfig() external view returns (InitialResourceBlock[] memory initialResourceBlocks);

    /// @notice Transfers game resources and workers from building to provided addresses
    /// @dev Removes resources+workers from building in single transaction
    /// @param workersReceiverAddress Workers receiver address (building or settlement)
    /// @param workersAmount Workers amount (in 1e18 precision)
    /// @param resourcesReceiverAddress Resources receiver address
    /// @param resourceTypes Resource types
    /// @param resourcesAmounts Resources amounts
    function removeResourcesAndWorkers(
        address workersReceiverAddress,
        uint256 workersAmount,
        address resourcesReceiverAddress,
        string[] calldata resourceTypes,
        uint256[] calldata resourcesAmounts
    ) external;

    /// @notice Calculates maximum amount of treasury by provided level
    /// @dev Can be used to determine maximum amount of treasury by any level
    /// @param level Building level
    /// @param maxTreasury Maximum amount of treasury
    function getMaxTreasuryByLevel(uint256 level) external view returns (uint256 maxTreasury);

    /// @notice Steals resources from treasury
    /// @dev Called by siege or building owner, in either case part of resources will be burned according to #registry.getRobberyFee
    /// @param to An address which will get resources
    /// @param amount Amount of resources to steal, 'to' will get only part of specified 'amount', some percent of specified 'amount' will be burned
    /// @return realAmount Real amount of resources from which stealing occurred (min(amount, treasury))
    function stealTreasury(
        address to,
        uint256 amount
    ) external returns (uint256 realAmount);

    /// @notice Burns building treasury
    /// @dev Can be called by world asset or building owner
    /// @param amount Amount of resources to burn from treasury
    function burnTreasury(
        uint256 amount
    ) external;

    /// @notice Increases additional workers capacity multiplier
    /// @dev Even though function is opened, it can be called only by world asset
    /// @param capacityAmount Capacity amount
    function increaseAdditionalWorkersCapacityMultiplier(uint256 capacityAmount) external;

    /// @notice Decreases additional workers capacity multiplier
    /// @dev Even though function is opened, it can be called only by world asset
    /// @param capacityAmount Capacity amount
    function decreaseAdditionalWorkersCapacityMultiplier(uint256 capacityAmount) external;

    /// @notice Calculates maximum amount of workers
    /// @dev Useful to determinate maximum amount of workers
    /// @return workersAmount Maximum amount of workers
    function getMaxWorkers() external view returns (uint256 workersAmount);

    /// @notice Calculates producing resource name for this building
    /// @dev Return value is value from #getConfig where 'isProduced'=true
    /// @return resourceName Name of producing resource
    function getProducingResourceName() external view returns (string memory resourceName);

    /// @notice Calculates treasury amount at specified time
    /// @dev Useful for determination how much treasury will be at specific time
    /// @param timestamp Time at which calculate amount of treasury in building. If timestamp=0 -> calculates as block.timestamp
    /// @return treasury Treasury amount at specified time
    function getTreasury(uint256 timestamp) external view returns (uint256 treasury);

    /// @notice Calculates if building is capable to accept resource
    /// @dev Return value based on #getConfig
    /// @param resourceName Name of resource
    /// @return isResourceAcceptable Is building can accept resource
    function isResourceAcceptable(string memory resourceName) external view returns (bool isResourceAcceptable);

    /// @notice Calculates max available for advanced production workers
    /// @dev Difference between getMaxWorkers() and getAdditionalWorkersFromAdditionalWorkersCapacityMultiplier()
    function getMaxAvailableForAdvancedProductionWorkers() external view returns (uint256);

    /// @notice Calculates additional workers 'granted' from capacity multiplier
    /// @dev Return value based on current advancedProduction.additionalWorkersCapacityMultiplier
    function getAdditionalWorkersFromAdditionalWorkersCapacityMultiplier() external view returns (uint256);

    /// @notice Returns building name
    /// @dev Same value as #assetName
    /// @return buildingName Building name
    function buildingName() external view returns (string memory buildingName);
}
","keccak256":"0x5bd3595704dec0ba8fdc52586567b935a4fef1d036814e2bdbc05e9082c735bb","license":"MIT"},"contracts/core/assets/epoch/IEpoch.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "../settlement/ISettlement.sol";
import "../tileCapturingSystem/ITileCapturingSystem.sol";
import "../tokens/resources/IResource.sol";

/// @title Epoch interface
/// @notice Functions to read state/modify state in order to get current epoch parameters and/or interact with it
interface IEpoch {
    /// @notice Emitted when epoch resource is created
    /// @param resourceAddress Resource address
    /// @param resourceName Resource name
    event NewResource(
        address resourceAddress,
        string resourceName
    );

    /// @notice Emitted when epoch units is created
    /// @param unitsAddress Units address
    /// @param unitsName Units name
    event NewUnits(
        address unitsAddress,
        string unitsName
    );

    /// @notice Emitted when epoch workers is created
    /// @param workersAddress Workers address
    event NewWorkers(
        address workersAddress
    );

    /// @notice Emitted when epoch prosperity is created
    /// @param prosperityAddress Prosperity address
    event NewProsperity(
        address prosperityAddress
    );

    /// @notice Emitted when epoch tile capturing system is created
    /// @param tileCapturingSystemAddress Tile capturing system address
    event NewTileCapturingSystem(
        address tileCapturingSystemAddress
    );

    /// @notice Emitted when #activateZone is called
    /// @param zoneAddress Zone address
    /// @param zoneId Zone index
    event NewZoneActivated(
        address zoneAddress,
        uint256 zoneId
    );

    /// @notice Emitted when #newAssetSettlement is called
    /// @param contractAddress Created settlement address
    /// @param scriptName Settlement type (BASIC/CULTISTS)
    /// @param zoneAddress Address of the zone where settlement is created
    /// @param position Position
    event NewSettlement(
        address indexed contractAddress,
        string scriptName,
        address zoneAddress,
        uint32 position
    );

    // State variables

    /// @notice An array of attached zones to the continent
    /// @dev Updated when #attachZoneToTheContinent is called
    function zones(uint256 index) external view returns (IZone);

    /// @notice Mapping containing settlement by provided x and y coordinates
    /// @dev Updated when new settlement is created
    function settlements(uint32 position) external view returns (ISettlement);

    /// @notice Most recent cultists summon time
    /// @dev Updated when #increaseTotalCultists is called
    function mostRecentCultistsSummonTime() external view returns (uint256);

    /// @notice Total cultists
    /// @dev Updated when #increaseTotalCultists or #decreaseTotalCultists is called
    function totalCultists() external view returns (uint256);

    /// @notice Workers token
    /// @dev Updated when #setWorkersContract is called
    function workers() external view returns (IWorkers);

    /// @notice Prosperity token
    /// @dev Updated when #setProsperityContract is called
    function prosperity() external view returns (IProsperity);

    /// @notice Mapping containing settlement address by provided banner id
    /// @dev Updated when #addUserSettlement is called
    function userSettlements(uint256 val) external view returns (ISettlement);

    /// @notice Mapping containing game resources by name
    /// @dev Updated when #addResource is called
    function resources(string memory name) external view returns (IResource);

    /// @notice Mapping containing units by name
    /// @dev Updated when #addUnit is called
    function units(string memory name) external view returns (IUnits);

    /// @notice Tile capturing system
    /// @dev Updated when #setTileCapturingSystemContract is called
    function tileCapturingSystem() external view returns (ITileCapturingSystem);

    // Functions

    /// @notice Proxy initializer
    /// @dev Called by factory contract which creates current instance
    /// @param epochNumber Epoch number
    function init(uint256 epochNumber) external;

    /// @notice Activates zone
    /// @dev Even though function is opened, it can be called only by mightyCreator
    /// @param zoneId Zone id
    function activateZone(uint16 zoneId) external;

    /// @notice Restores settlement from previous epoch by provided position
    /// @dev Any address can restore user settlement
    /// @param position Position
    function restoreSettlement(
        uint32 position
    ) external;

    /// @notice Creates new user settlement
    /// @dev Bless tokens will be deducted from msg.sender
    /// @param position Position
    /// @param ownerTokenId Banners token id which will represent to which settlement will be attached to
    /// @param settlementPrice Settlement price
    /// @param settlementPurchaseTime Settlement purchase time
    /// @return settlementAddress Settlement address
    function newSettlement(
        uint32 position,
        uint256 ownerTokenId,
        uint256 settlementPrice,
        uint256 settlementPurchaseTime
    ) external returns (address settlementAddress);

    /// @notice Creates settlement by type
    /// @dev Even though function is opened, it can be called only by world asset
    /// @param ownerTokenId Banners token id which will represent to which settlement will be attached to
    /// @param position Position
    /// @param assetName Settlement type (BASIC/CULTISTS)
    /// @param performAttachmentValidation Whether is to perform attachment validation or not (CULTISTS or restored BASIC may not be attached to anything)
    function newAssetSettlement(
        uint256 ownerTokenId,
        uint32 position,
        string memory assetName,
        bool performAttachmentValidation
    ) external returns (address);

    /// @notice Summons cultists in specified zones
    /// @dev Batch cultists summon
    /// @param zoneIds Zone ids
    function summonCultistsBatch(
        uint16[] memory zoneIds
    ) external;

    /// @notice Increases total cultists
    /// @dev Even though function is opened, it can only be called by world asset
    /// @param cultistsArmyAddress Cultists army address
    /// @param value Amount of cultists minted
    function increaseTotalCultists(
        address cultistsArmyAddress,
        uint256 value
    ) external;

    /// @notice Decreases total cultists
    /// @dev Even though function is opened, it can only be called by world asset
    /// @param cultistsArmyAddress Cultists army address
    /// @param value Amount of cultists burned
    function decreaseTotalCultists(
        address cultistsArmyAddress,
        uint256 value
    ) external;
}
","keccak256":"0xecc1d8ca9813a3c488af3cb5b4aa02d7d6b6ded3ff4dabcd382098137ce88cde","license":"MIT"},"contracts/core/assets/settlement/ISettlement.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "../building/IBuilding.sol";
import "../army/IArmy.sol";
import "../siege/ISiege.sol";
import "../zone/IZone.sol";

/// @title Settlement interface
/// @notice Functions to read state/modify state in order to get current settlement parameters and/or interact with it
interface ISettlement {
    /// @notice Emitted when new building is placed, all building are placed on settlement creation
    /// @param contractAddress New building address
    /// @param scriptName Building name
    event NewBuilding(address contractAddress, string scriptName);

    /// @notice Emitted when settlements army is created, is it created on settlement creation
    /// @param armyAddress New army address
    /// @param position Position
    event NewArmy(address armyAddress, uint32 position);

    /// @notice Emitted when siege is created on settlement if not present. During settlements lifetime multiple sieges can be created (one after another, not multiple simultaneously)
    /// @param siegeAddress New siege address
    event SiegeCreated(address indexed siegeAddress);

    /// @notice Emitted when #addGovernor or #removeGovernor is called
    /// @param currentEpoch Current governor epoch
    /// @param governorAddress Address of the governor event is applicable
    /// @param status Is governor became active/inactive
    event GovernorChanged(uint256 indexed currentEpoch, address indexed governorAddress, bool status);

    /// @notice Emitted when #removeGovernors is called
    /// @param currentEpoch New governor epoch
    event NewSettlementEpoch(uint256 currentEpoch);

    // State variables

    /// @notice Zone to which this settlement belongs
    /// @dev Immutable, initialized on the settlement creation
    function currentZone() external view returns (IZone);

    /// @notice Banner token id to which current settlement belongs
    /// @dev Immutable, initialized on the settlement creation
    function ownerTokenId() external view returns (uint256);

    /// @notice Siege of the settlement
    /// @dev If any army is sieging settlement not address(0), otherwise address(0)
    function siege() external view returns (ISiege);

    /// @notice Mapping containing settlements buildings
    /// @dev Types of buildings supported can be queried from registry
    function buildings(string memory buildingName) external view returns (IBuilding);

    /// @notice Current governors epoch
    /// @dev Modified when #removeGovernors is called
    function currentGovernorsEpoch() external view returns (uint256);

    /// @notice Current settlements governors
    /// @dev Modified when #addGovernor or #removeGovernor is called
    function governors(uint256 epoch, address isGovernor) external view returns (bool);

    /// @notice Settlements army
    /// Immutable, initialized on the settlement creation
    function army() external view returns (IArmy);

    /// @notice Extra prosperity amount gained from demilitarization of any army on this settlement
    /// @dev Used for determination amount of real prosperity this settlement has
    function extraProsperity() external view returns (uint256);

    /// @notice Position on which settlement is created
    /// @dev Immutable, initialized on the settlement creation
    function position() external view returns (uint32);

    // Functions

    /// @notice Proxy initializer
    /// @dev Called by factory contract which creates current instance
    /// @param createdWithOwnerTokenId Banner token id to which current settlement belongs
    /// @param zoneAddress Zone address to which this settlement belongs
    /// @param settlementPosition Position on which settlement is created
    function init(
        uint256 createdWithOwnerTokenId,
        address zoneAddress,
        uint32 settlementPosition
    ) external;

    /// @notice Withdraws game resources from settlement to specified address
    /// @dev In case if someone accidentally transfers game resource to the settlement
    /// @param resourceName Game resource name
    /// @param to Address that will receive resources
    /// @param amount Amount to transfer
    function withdrawResources(
        string memory resourceName,
        address to,
        uint256 amount
    ) external;

    /// @notice Transfers game resources from msg.sender and workers from settlement to building
    /// @dev Assigns resources+workers to building in single transaction
    /// @dev If resourcesOwner == address(0) -> resources will be taken from msg.sender
    /// @dev If resourcesOwner != address(0) and resourcesOwner has given allowance to msg.sender >= resourcesAmount -> resources will be taken from resourcesOwner
    /// @param buildingAddress Building address
    /// @param workersAmount Workers amount (in 1e18 precision)
    /// @param resourceTypes Resource types
    /// @param resourcesAmounts Resources amounts
    function assignResourcesAndWorkersToBuilding(
        address resourcesOwner,
        address buildingAddress,
        uint256 workersAmount,
        string[] memory resourceTypes,
        uint256[] memory resourcesAmounts
    ) external;

    /// @notice Creates new building
    /// @dev All buildings are created on settlement creation
    /// @param buildingName Building name
    /// @return buildingAddress Address of created building
    function newBuilding(string memory buildingName) external returns (address buildingAddress);

    /// @notice Calculates current fort health and damage dealt at specified timestamp
    /// @dev Uses fort production and siege parameters to forecast health and damage will be dealt at specified time
    /// @param timestamp Time at which calculate parameters
    /// @return currentHealth Health value at specified time
    /// @return damage Amount of damage dealt from fort.production.lastUpdateState to specified timestamp
    function calculateCurrentHealthAndDamage(uint256 timestamp)
        external
        view
        returns (uint256 currentHealth, uint256 damage);

    /// @notice Updates settlement health to current block
    /// @dev Can be called by everyone
    function updateCurrentHealth() external;

    /// @notice Creates empty siege
    /// @dev Can be called by everyone
    function createSiege() external;

    /// @notice Updates fort health
    /// @dev Even though function is opened it can be called only by world or world asset
    /// @param healthDiff Health delta between current value and new value
    /// @param isProduced Banner, whether health is produced or removed
    function updateFortHealth(uint256 healthDiff, bool isProduced) external;

    /// @notice Applies production of every building which produces prosperity
    /// @dev Can be used by everyone
    function massUpdate() external;

    /// @notice Calculates current prosperity at specified timestamp
    /// @dev Uses buildings productions to forecast amount of prosperity will settlement will have at specified time
    /// @param timestamp Time at which calculate current prosperity
    /// @return currentProsperity Amount of prosperity at specified time
    function accumulatedCurrentProsperity(uint256 timestamp) external view returns (int256 currentProsperity);

    /// @notice Calculates total siege power presented at current time
    /// @dev Updated when army add/remove units from siege
    /// @return currentSiegePower Amount of total siege power at current time
    function getCurrentSiegePower() external view returns (uint256 currentSiegePower);

    /// @notice Calculates current settlement owner
    /// @dev Settlements owner is considered an address, which holds ownerTokenId NFT
    /// @return settlementOwner Settlement owner
    function getSettlementOwner() external view returns (address settlementOwner);

    /// @notice Calculates whether provided address is settlement ruler or not
    /// @dev Settlements ruler is an address which owns settlement or an address(es) by which settlement is/are governed
    /// @param potentialRuler Address to check
    /// @return isRuler Banner, whether specified address is ruler or not
    function isRuler(address potentialRuler) external view returns (bool isRuler);

    /// @notice Extends current settlement prosperity by specified amount
    /// @dev Even though function is opened it can be called only by world or world asset
    /// @param prosperityAmount Amount of prosperity to which extend current prosperity
    function extendProsperity(uint256 prosperityAmount) external;

    /// @notice Begins tile capture
    /// @param position Position
    /// @param prosperityStake Prosperity stake
    function beginTileCapture(uint32 position, uint256 prosperityStake) external;

    /// @notice Cancels tile capture
    /// @param position Position
    function cancelTileCapture(uint32 position) external;

    /// @notice Gives up captured tile
    /// @param position Position
    function giveUpCapturedTile(uint32 position) external;

    /// @notice Claims captured tile
    /// @param position Position
    function claimCapturedTile(uint32 position) external;
}
","keccak256":"0xf58c3c5a8761258bfa30bfe94babaf12b2f239650775cec6d68086c5ffb8841a","license":"MIT"},"contracts/core/assets/settlementsMarket/ISettlementMarket.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "../zone/IZone.sol";

/// @title Zone settlements market interface
/// @notice Functions to read state/modify state in order to buy settlement
interface ISettlementsMarket {
    /// @notice Emitted when #buySettlement is called
    /// @param settlementAddress Settlement address
    /// @param settlementCost Settlement cost
    event SettlementBought(address settlementAddress, uint256 settlementCost);

    // State variables

    /// @notice Zone to which this market belongs
    /// @dev Immutable, initialized on the market creation
    function currentZone() external view returns (IZone);

    /// @notice Time at which market was created
    /// @dev Immutable, initialized on the market creation
    function marketCreationTime() external view returns (uint256);

    // Functions

    /// @notice Proxy initializer
    /// @dev Called by factory contract which creates current instance
    /// @param zoneAddress Zone address
    function init(
        address zoneAddress
    ) external;

    /// @notice Buys settlement in zone
